#include <iostream>
#include <vector>

using namespace std;

class Solution {
public:
    int chalkReplacer(vector<int>& chalk, int k) {
        long long sum = 0;
        for (int x : chalk)
            sum += x;
        k %= sum;
        if (k == 0)
            return chalk.size() - 1;
    	for(int i = 0; i < chalk.size(); i++)
    	{
            k -= chalk[i];
            if (k <= 0)
                return i;
    	}
        return -1;
    }
};

